import React from "react";
// import "react-dates/initialize";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import "react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css";
import "react-bootstrap-table-next/dist/react-bootstrap-table2.min.css";
import "bootstrap/dist/css/bootstrap.min.css";
// import "font-awesome/css/font-awesome.min.css";
import { PaginationProvider, PaginationListStandalone } from 'react-bootstrap-table2-paginator';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
import ToolkitProvider, { Search } from 'react-bootstrap-table2-toolkit';
import { DropdownMenu, MenuItem } from 'react-bootstrap-dropdown-menu';
import "../App.css";

export default class CityFilter extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        selectedOption: null,
      };
    }

    handleChange = selectedOption => {
      this.setState({ selectedOption });
      console.log(`Option selected:`, selectedOption.target.value);
    };

    render() {
      const { selectedOption } = this.state;
      const options = [
        { value: 'Mumbai', label: 'Mumbai' },
        { value: 'Bangalore', label: 'Bangalore' },
        { value: 'Kolkata', label: 'Kolkata' },
        { value: 'Delhi', label: 'Delhi' },
        { value: 'Chennai', label: 'Chennai' },
      ];

      return (
        <div>
          <div lassName="ml-3 mr-3">
                      <select
                        className="col-xs-12 col-sm-6 col-md-8 filter-box"
                        value={selectedOption}
                        onChange={this.handleChange}
                        // options={options}
                      >
                        <option value="" selected disabled hidden>
                          Choose Attribute
                        </option>
                        <option value="Mumbai">Mumbai</option>
                        <option value="Kolkata">Kolkata</option>
                        <option value="Bangalore">Bangalore</option>
                        <option value="Delhi">Delhi</option>
                        <option value="Chennai">Chennai</option>
                      </select>
                    </div>
        </div>
      );
    }
  }
